package main

/*
  Day 6. Part 1:
*/

import (
	"fmt"
	"strings"
)

func main() {
	var inputs string = ""
	stream := strings.Split(inputs, "")
	fmt.Println(stream)

	for x := 0; x <= (len(stream) - 4); x++ {
		m1 := stream[x]
		m2 := stream[x+1]
		m3 := stream[x+2]
		m4 := stream[x+3]

		if m1 != m2 && m1 != m3 && m1 != m4 && m2 != m3 && m2 != m4 && m3 != m4 {
			fmt.Println(m1, m2, m3, m4)
			fmt.Println("Result: ", x+4)
			break
		}
	}
}
