package main

/*
  Day 6. Part 2:
*/

import (
	"fmt"
	"strings"
)

func main() {
	const marker = 14
	var inputs string = ""
	stream := strings.Split(inputs, "")

	for x := 0; x <= (len(stream) - marker); x++ {
		array := stream[x : x+marker]
		if !hasDuplicates(array) {
			fmt.Println("Result: ", x+marker)
			break
		}
	}
}

func hasDuplicates(s []string) bool {
	for index, item := range s {
		for x := 0; x < len(s); x++ {
			if x == index {
				continue
			}
			if item == s[x] {
				return true
			}
		}
	}
	return false
}
